import Link from 'next/link';
import styled from 'styled-components';
import { mediaQuery } from '../utils/style';

const linkStyle = {
  marginRight: 20,

  textDecoration: 'none'
};

const logoStyle = {
    maxWidth: '100%',
    maxHeight: '100%',
};

const HeaderBox = styled.div`
  background-color: #4a4a4a;
  height: 100px;
`;

const LogoBox = styled.div`
  width: 100px;
  float: left;

  ${mediaQuery.lessThan('medium')`
    width: 80px;
    float: left;
  `}
`;

const hrStyle = {
  borderTop: '2px solid #14a3bb',
  verticleAlign: 'bottom',
};

const HrBox = styled.div`
  border: 2px solid #14a3bb;


  ${mediaQuery.lessThan('medium')`
    border-bottom: 2px solid #14a3bb;

  `}
`;

const fontStyle = {
  color: '#FFF',
  //fontSize: 20,
  marginRight: 20,
}
const FontBox = styled.div`
  font-size: 25px;
  float: right;

  ${mediaQuery.lessThan('medium')`
  font-size: 0px;
  float: right;

  `}
`;
/*
      <Link href="/">
        <a style={linkStyle}><font style={fontStyle}>Home</font></a>
      </Link>
      <Link href="#about">
        <a style={linkStyle}><font style={fontStyle}>About</font></a>
      </Link>
      <Link href="#feature">
        <a style={linkStyle}><font style={fontStyle}>Features</font></a>
      </Link>
      <Link href="#partners">
        <a style={linkStyle}><font style={fontStyle}>Partners</font></a>
      </Link>
*/
const Header = () => (
  <div>
      <HeaderBox>
        <Link href="/">
          <a> <LogoBox><img style={logoStyle} src="../static/logo.png"/></LogoBox></a>
        </Link>

        <FontBox>
          <Link href="/amazon">
            <a style={linkStyle}><font style={fontStyle}>Amazon</font></a>
          </Link>
          <Link href="/naver">
            <a style={linkStyle}><font style={fontStyle}>Naver</font></a>
          </Link>
          <Link href="/shopee">
            <a style={linkStyle}><font style={fontStyle}>Shopee</font></a>
          </Link>
        </FontBox>
      </HeaderBox>


  </div>


);

export default Header;
