  <div id="about">
    <h1>What is Project River</h1>

    <ul style= {liStyle}>
      <li>
        <Box>
          <h2 style={h2Style}>Market Intelligence</h2>
        </Box>
      </li>

    </ul>

  </div>
);

export default About;


