const imgStyle = {
    width: '100%',
};
const txtStyle1 = {
  width: 200,
  height: 100,
  marginRight: 100,
  fontSize: 30,
  backgroundColor: '#52c1be',
}
const txtStyle2 = {
  width: 200,
  height: 100,
  marginRight: 100,
  fontSize: 30,
}
const txtStyle3 = {
  width: 200,
  height: 100,
  marginRight: 100,
  fontSize: 30,
}
const divStyle = {
  //paddingBottom: 100,
  position: 'relative',
}
const txtDivStyle = {
  position: 'absolute',
	top: '40%',
  left: '15%',
}


const Banner = props => (
  <div style={divStyle}>
    <img style={imgStyle} src="../static/banner.jpg" alt="Banner Image"/>
    <div style={txtDivStyle}>
      <text style={txtStyle1}><a href="/amazon">Amazon</a></text>
      <text style={txtStyle2}>Naver Shopping</text>
      <text style={txtStyle3}>Shopee</text>
    </div>
  </div>
);
