  height: 165px;

  ${mediaQuery.lessThan('medium')`
    padding: 20px;
    border: 1px solid #DDD;
    height: 200px;

  `}
`;

const layoutStyle = {
  padding: 20,
  border: '1px solid #DDD',
  height: 165,
};

const PhraseLayout = props => (
  <div>
    <Contents>
      <Phrase />
      {props.children}
